<?php //微博收藏class FriendshipsApi extends Api{		//关注某用户	function create(){		$result = D('Follow','weibo')->dofollow($this->mid,$this->user_id);		return $result;	}		//取消关注	function destroy(){		$result = D('Follow','weibo')->unfollow($this->mid,$this->user_id);		return $result;	}		// 获取关注详情	function show(){		return getFollowState($this->user_id, $this->mid);	}		}?>