<?phpimport('admin.Action.AdministratorAction');class AdminAction extends AdministratorAction{		function index(){		if($_POST){			$data['todaytopic'] = h($_POST['todaytopic']);			$res = model('Xdata')->lput('weibo', $data);			$this->assign('jumpUrl', U('weibo/Admin/index'));			if ($res) 				$this->success('保存成功');			else 				$this->error('保存失败');		}else{			$res = model('Xdata')->lget('weibo');			$this->assign($res);			$this->display();		}	}		function weibolist(){		$list = D('Weibo')->order('weibo_id DESC')->findpage(20);		$data['list'] = $list;		$this->assign($data);		$this->display('list');	}		//微博操作	function operate(){		$strType = $_POST['dotype'];		if($strType=='del'){			echo D("Operate")->deleteMini(intval($_POST['weibo_id']),intval($_POST['uid']));		}	}}?>